import {Component} from '@angular/core';
import {FormControl} from '@angular/forms';

interface Transport {
    name: string;
    icon: string;
    color: string;
    type: string | null;
}
@Component({
    selector: 'hc-picklist-templates-example',
    templateUrl: './picklist-templates-example.component.html',
    styleUrls: ['./picklist-templates-example.component.scss'],
    standalone: false
})
export class PicklistTemplatesExampleComponent {
    readonly selected = new FormControl([], {nonNullable: true});
    transportOptions: Transport[] = [
        {name: 'Train', icon: 'train', color: 'green', type: 'land'},
        {name: 'Bus', icon: 'bus', color: 'blue', type: 'land'},
        {name: 'Ferry', icon: 'ship', color: 'teal', type: 'sea'},
        {name: 'Space Shuttle', icon: 'space-shuttle', color: 'slate-gray', type: 'air'},
        {name: 'Plane', icon: 'plane', color: 'deep-red', type: 'air'},
        {name: 'Rocket', icon: 'rocket', color: 'orange', type: 'air'},
        {name: 'Sled', icon: 'snowflake', color: 'purple', type: null}
    ];

    groupByFn = (item: Transport): string | null => item.type;

    customSearchFn(term: string, item: Transport): boolean {
        term = term?.toLowerCase();
        return item.name.toLowerCase().indexOf(term) > -1 || (!!item.type && item.type.toLowerCase().indexOf(term) > -1);
    }

    sortFn(a: {label: string}, b: {label: string}): number {
        return a.label.localeCompare(b.label);
    }
}

$slate-gray-100: #f0f3f6;
$blue: #00aeff;
$green: #00a859;
$purple: #6e53a3;
$teal: #00acac;
$orange: #f8961d;
$dark-blue: #006d9a;
$offblack: #333;
$white: #fff;
$deep-red: #cc2027;
$slate-gray-600: #4f565c;
$slate-gray-100: #f0f3f6;
$gray-300: #ccc;

.group-row {
    font-size: 12px;
    padding: 5px 20px;
    margin: -8px -10px;
    text-transform: capitalize;

    .transport-group-icon {
        float: right;
        color: $blue;
        cursor: pointer;
    }
}

.ng-value-label hc-icon {
    margin-right: 3px;
}

.opt-row {
    display: flex;
    align-items: center;
}

.ico-wrapper {
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    height: 28px;
    width: 28px;
    margin-right: 7px;
    color: $white;

    &.blue {
        background-color: $blue;
    }
    &.green {
        background-color: $green;
    }
    &.teal {
        background-color: $teal;
    }
    &.purple {
        background-color: $purple;
    }
    &.orange {
        background-color: $orange;
    }
    &.slate-gray {
        background-color: $slate-gray-600;
    }
    &.deep-red {
        background-color: $deep-red;
    }
}

.custom-header {
    background-color: $dark-blue;
    color: white;
    padding: 7px 12px;
    border-radius: 4px;
    margin-bottom: 5px;
    line-height: 1.3;
    .header-text {
        font-weight: 600;
    }
    .template-callout {
        color: $slate-gray-100;
    }
}

.fa-suitcase-rolling {
    margin-right: 5px;
}

.search-for {
    float: right;
}

.custom-toolbar,
.custom-footer {
    border: 1px solid $gray-300;
    background-color: $slate-gray-100;
    color: $offblack;
    padding: 5px 12px;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    margin: -5px 0;
    width: 100%;
    font-size: 11px;
    display: flex;
    justify-content: space-between;
}

.custom-footer {
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
}

.custom-item {
    border: 2px dashed $gray-300;
    padding: 4px;
    border-radius: 4px;
    display: flex;
    flex-direction: column;

    & > div {
        margin-bottom: 5px;
    }

    .fa {
        font-size: 24px;
        margin-bottom: 10px;
    }
}
